import "../css/home.css";
import "../css/dashbord.css";
import Sidebar from "../component/admin/sidebar";
import Navbar from "../component/navbar";
import Formlogin from "../component/com-singin/formlogin";
import useToken from "./useToken";
import { Fragment } from "react";
import Axios from "axios";
import { useState } from "react";
import { Link, useMatch, useResolvedPath, useLocation } from "react-router-dom";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

function Transaction() {
  const location = useLocation();
  const [userid, setuserid] = useState(location.state.id);
  const { token, setToken } = useToken();
  const [userlist, setuserlist] = useState([]);
  const [age, setAge] = useState("");
  if (!token) {
    return (
      <Fragment>
        <Navbar></Navbar>
        <Formlogin setToken={setToken} />
      </Fragment>
    );
  }

  Axios.get("http://localhost:3001/user").then((response) => {
    setuserlist(response.data);
  });

  const user = userlist.find((obj) => {
    return obj.user_id === userid;
  });
  //console.log(user)

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  return (
    <div>
      <Navbar></Navbar>
      <div class="landingpage">
        <div class="container1">
          <Sidebar></Sidebar>
          <main>
            <div class="recent-orders">
              {/* render single object */}
              {user && (
                <div>
                  <h1>name: {user.user_name}</h1>
                </div>
              )}

              <FormControl sx={{ m:1, minWidth: 300 }} size="small">
                <InputLabel id="demo-select-small">port number</InputLabel>
                <Select
                  labelId="demo-select-small"
                  id="demo-select-small"
                  value={age}
                  label="Age"
                  onChange={handleChange}
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  <MenuItem value={10}>Ten</MenuItem>
                  <MenuItem value={20}>Twenty</MenuItem>
                  <MenuItem value={30}>Thirty</MenuItem>
                </Select>
              </FormControl>
            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default Transaction;
