import React from "react";
import Axios from "axios";
import { useState, useEffect } from "react";
import Navbar from "../navbar";
import Sidebar from "./sidebar";
import { useLocation } from "react-router-dom";

import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

function GetUserdata() {
  const [userlist, setuserlist] = useState([]);
  const location = useLocation();
  const [userid, setuserid] = useState(location.state.id); //sent from dashbord
  const [port, setport] = useState("");
  useEffect(() => {
    Axios.get("http://localhost:3001/transaction").then((res) => {
      setuserlist(res.data);
    });
  });
  //console.log(userlist)

  const user = userlist.filter((obj) => {
    return obj.user_id === userid;
  }); 
//console.log(user);
  //console.log(Object.values(user));

  const handleChange = (event) => {
    setport(event.target.value);
  };


  return (
    <div>
      <Navbar></Navbar>
      <div className="landingpage">
        <div className="container1">
          <Sidebar></Sidebar>
          <main>
            <div className="recent-orders">
                <div >

                </div>
              <FormControl sx={{ m: 1, minWidth: 300 }} size="small">
                <InputLabel id="demo-select-small">port number</InputLabel>
                <Select
                  labelId="demo-select-small"
                  id="demo-select-small"
                  value={port}
                  label="port number"
                  onChange={handleChange}
                >
                {user.map((val)=>{
                    return(
                        <MenuItem value={val.port_number}>{val.port_number}</MenuItem>
                    );  
                })}
                </Select>
              </FormControl>

              <table>
                <thead>
                  <tr>
                    <th>port number</th>
                    <th>time </th>
                    <th>balance</th>
                    <th>profit</th>
                  </tr>
                </thead>
                <tbody>
                  {user.map((val) => {
                    return (
                      <tr>
                        <td>{val.port}</td>
                        <td>{val.user_name}</td>
                        <td>{val.email}</td>
                        <td>{val.password} </td>

                        <td>
                          <Link class="danger" to="/">EDIT /</Link>
                          <Link class="danger" to="/"> DEL</Link>
                        </td>
                        <td>
                          <Link to="/Transaction" class="warning" state={{ id: val.user_id }}>
                            Show all
                          </Link>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>

            </div>
          </main>
        </div>
      </div>
    </div>
  );
}

export default GetUserdata;
